#Problem 3import numpy as npimport cmathimport matplotlib.pyplot as pltlarge_number = 100 #number of iterations that we can call approx infiniteN = 5000x1 = np.linspace(-2, 2, N+1)x, y = np.meshgrid(x1, x1)j = complex(0, 1)y = y*jc = x+y #set up all possible combos of complex numbers on gridz = np.zeros((N+1,N+1)) #initial valuefor i in range(1, large_number+1):    z = z**2+cz = abs(z)ztest = z<=2mandelbrot = c*ztestplt.scatter(mandelbrot.real, mandelbrot.imag, c='k')plt.xlabel("Real")plt.ylabel("Imaginary")plt.show()