#Problem 4import numpy as np#Part aprint("Part A: ")a = 0.001b=1000c = 0.001x1 = (-b+np.sqrt(b**2-4*a*c))/(2*a)x2 = (-b-np.sqrt(b**2-4*a*c))/(2*a)print(x1)print(x2)#Part bprint("Part B: ")x1 = 2*c/(-b-np.sqrt(b**2-4*a*c))x2 = 2*c/(-b+np.sqrt(b**2-4*a*c))print(x1)print(x2)#Part cprint("Part C: ")a = np.float64(0.001)b=np.int64(1000) #64 bit doesn't help much for this case but is generally extra protectionc = np.float64(0.001) if np.sign(b) == (-1): #want exclusively same-sign addition where possible to avoid large # subtraction error     x1 = (-b+np.sqrt(b**2-4*a*c))/(2*a)    x2 = 2*c/(-b+np.sqrt(b**2-4*a*c))else:    x1 = 2*c/(-b-np.sqrt(b**2-4*a*c))    x2 = (-b-np.sqrt(b**2-4*a*c))/(2*a)print(x1)print(x2)